import "bootstrap/dist/css/bootstrap.min.css";
import InputBlock from "./InputBlock";
import {useState, useEffect, useContext} from 'react'
import TemplaterService, { ITemplater } from "../services/TemplaterService";
import { TemplateContext } from "../context/TemplateContext";

type ParamInterface = {
  key: number
  name: string
  value: string
}

const TableTemplate = () => {

  const [params, setParams] = useState<string[]>([]);

  const {inputFilename} = useContext(TemplateContext)

  useEffect(() => {
    TemplaterService.scan(inputFilename).then((response) => {
      setParams(response.data.params)
    });
  });

  const [paramsData, setParamsData] = useState<ParamInterface[]>([]);

  const AddParams = (name: string, value: string, key: number) => {
        var params: ParamInterface[] = paramsData;

        params = params.filter(item => item.key === key ? false : true)

        setParamsData([...params, {name, value, key}])
  }

  const send = () => {
    var data:ITemplater;

    var outputFilename:string = inputFilename;

    var splitted = outputFilename.split(".", 3); 
    console.log(splitted)

    data = {
      file_name_input: inputFilename,
      file_name_output: outputFilename,
      templ_date: []
    }

    paramsData.map(item => {
      data.templ_date.push({key: item.name, value: item.value})
    })

    TemplaterService.templ(data)
      .then(() => {
      })
      .catch(() => {
      });
  }

  return (
    <>
      <form>
        {params.map((item, index) => <InputBlock item={item} index={index} addParam={AddParams}/>)}
      </form>
      <button
        className="btn btn-success btn-sm"
        onClick={send}
        >
        Дальше
      </button>
    </>
  );
};

export default TableTemplate;
