import React, {createContext, useState} from 'react'

interface ITemplateContext {
  inputFilename: string
  fileUpload: boolean
  paramsInput: boolean
  setFilename: (filename:string) => void
}

export const TemplateContext = createContext<ITemplateContext>({
  inputFilename: '',
  fileUpload: false,
  paramsInput: false,
  setFilename: () => {}
})

export const TemplateState = ({ children }: {children: React.ReactNode}) => {
  const [inputFilename, setFileName] = useState('')
  const [fileUpload, setFileUpload] = useState(false)
  const [paramsInput, setParamsInput] = useState(false)

  const setFilename = (filename:string) => {
    setFileName(filename)
    setFileUpload(true)
  }

  return (
    <TemplateContext.Provider value={{ inputFilename, fileUpload, paramsInput, setFilename }}>
      { children }
    </TemplateContext.Provider>
  )
}
