import React, {createContext, useState} from 'react'

interface ITemplateContext {
  filename: string
  setFilename: () => void
}

export const TemplateContext = createContext<ITemplateContext>({
  filename: '',
  setFilename: () => {}
})

export const TemplateState = ({ children }: {children: React.ReactNode}) => {
  const [filename, setFileName] = useState(0)

  const setFilename = () => setFileName(prev => prev + 1)


  return (
    <TemplateContext.Provider value={{ filename, setFilename }}>
      { children }
    </TemplateContext.Provider>
  )
}
