import React, {createContext, useState} from 'react'

interface ITemplateContext {
  modal: number
  open: () => void
  close: () => void
}

export const TemplateContext = createContext<ITemplateContext>({
  modal: number,
  open: () => {},
  close: () => {}
})

export const TemplateState = ({ children }: {children: React.ReactNode}) => {
  const [modal, setModal] = useState(0)

  const open = () => setModal(prev => prev + 1)

  const close = () => setModal(prev => prev + 1)

  return (
    <TemplateContext.Provider value={{ modal, open, close }}>
      { children }
    </TemplateContext.Provider>
  )
}
