import "bootstrap/dist/css/bootstrap.min.css";
import InputBlock from "./InputBlock";
import {useState} from 'react'

type ParamInterface = {
  key: number
  name: string
  value: string
}


const TableTemplate = () => {

  var paramsData: ParamInterface[] = [];

  const AddParams = (name: string, value: string, key: number) => {
      if (paramsData.length === 0) {
          paramsData.push({
            name: name,
            value: value,
            key: key
          })
      } else {
        paramsData.forEach(item => {
          item.value = item.key === key ? value : item.value
        })
      }
  }

  const params = [
    'test',
    'test2'
  ]


  return (
    <>
      <div>{paramsData}</div>
    <form>
      {params.map((item, index) => <InputBlock item={item} index={index} addParam={AddParams}/>)}
    </form>
    </>
  );
};

export default TableTemplate;
