import React  from 'react';
import './App.css';
import General from './components/General';
import { useState, useContext } from "react";
import { TemplateState , TemplateContext} from './context/TemplateContext'

function App() {
  const [step, setStep] = useState<number>(0);
  const {modal, open, close} = useContext(TemplateContext)

  return (
    <TemplateState>
          <div className="App">
      <header className="App-header">
        <p>
          Шаблонизатор
        </p>
      </header>

      <div className="App-Card">
          <General step={step}/>
          <button
            className="btn btn-success btn-sm"
            onClick={() => setStep(prev => {
              if (prev === 0) open() 
              if (prev === 1) close() 
              return prev < 2 ? prev + 1 : 0
            })}
            >
            {step === 2 ? 'Начать заново' : 'Дальше'}
          </button>
      </div>
    </div>
    </TemplateState>
  );
}

export default App;
