import "bootstrap/dist/css/bootstrap.min.css";
import InputBlock from "./InputBlock";
import {useState} from 'react'

type ParamInterface = {
  key: number
  name: string
  value: string
}


const TableTemplate = () => {

  const [paramsData, setParamsData] = useState<ParamInterface[]>([]);

  const AddParams = (name: string, value: string, key: number) => {
    setParamsData(prev => [...prev, {name, value, key}])
        // paramsData.forEach(item => {
        //   if (paramsData.length === 0 || item.key !== key) {
        //       setParamsData(prev => [...prev, {name, value, key}])
        //   } else {

        //   }
        // })

  }

  const params = [
    'test',
    'test2'
  ]


  return (
    <>
      <div>{paramsData.length}</div>
    <form>
      {params.map((item, index) => <InputBlock item={item} index={index} addParam={AddParams}/>)}
    </form>
    </>
  );
};

export default TableTemplate;
